import React, { useState, useEffect } from "react";
import "./App.css"; // Add your CSS styling here
import { Star } from 'lucide-react';
import Confetti from 'react-confetti';

// Import all the images
import owlImage from "./assets/owl.png";
import egg1Image from "./assets/WhatsApp_Image_2024-12-01_at_6.33.19_PM-removebg-preview.png";
import egg2Image from "./assets/WhatsApp_Image_2024-12-01_at_6.33.49_PM-removebg-preview.png";
import egg3Image from "./assets/WhatsApp_Image_2024-12-01_at_6.35.26_PM-removebg-preview.png";
import backgroundImage from "./assets/yvns_xtdx_210511.jpg"; // Background image
import treeImage from "./assets/xxk3_avc4_190721-removebg-preview.png"; // Hiding object (Tree)
import bushImage from "./assets/od0a_ebuj_230706-removebg-preview.png"; // Hiding object (Bush)
import rockImage from "./assets/Cartoon_Pine_Tree_Clipart_PNG_Images__Tree_For_Cartoon_Isolated_On_White_Background__Tree__Bush__Cartoon_PNG_Image_For_Free_Download-removebg-preview.png"; // Hiding object (Rock)
import brokenEggImage from "./assets/broken-egg.png"; // Broken egg image

function App() {
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(60);
  const [gameStarted, setGameStarted] = useState(false);
  const [currentLineIndex, setCurrentLineIndex] = useState(0);
  const [hintUsed, setHintUsed] = useState(false); // To track if hint was used
  const [hintAvailable, setHintAvailable] = useState(false); // To track if hint can be used
  const [eggsFound, setEggsFound] = useState(0); // Track number of eggs found
  const totalEggs = 3; // Total number of eggs to find
  const [showBrokenEgg, setShowBrokenEgg] = useState(false); // Show broken egg when all eggs are found
  const [owlMessage, setOwlMessage] = useState("Click on the object to find Easter eggs"); // Combined message displayed by the owl
  const [dialogueText, setDialogueText] = useState(
    "Identify the Easter egg locations! Click the owl for hints (1 hint = deduct 30 points)"
  );
  const [windowSize, setWindowSize] = useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  useEffect(() => {
    const handleResize = () => {
      setWindowSize({
        width: window.innerWidth,
        height: window.innerHeight,
      });
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const definitionLines = [
    "Under the bush",
    "Under the Parrot",
  ];

  const credits = [
    "Congratulations on finding all the Easter Eggs!",
    "Created with ❤️ by Your Name",
    "Thanks for Playing!",
    "Special Thanks to:",
    "All the Easter Egg Hunters",
    " 2024 Easter Egg Hunt"
  ];

  // Timer logic
  useEffect(() => {
    if (gameStarted && timeLeft > 0) {
      const timerInterval = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
      return () => clearInterval(timerInterval);
    }
    if (timeLeft === 0) {
      endGame();
    }
  }, [gameStarted, timeLeft]);

  const startGame = () => {
    setScore(0); // Reset score
    setTimeLeft(60); // Reset timer
    setGameStarted(true);
    setHintUsed(false); // Reset hint usage
    setHintAvailable(true); // Allow hint after the game starts
    setEggsFound(0); // Reset eggs found
    setShowBrokenEgg(false); // Hide broken egg
    setOwlMessage("Click on the object to find Easter eggs"); // Reset owl message
    startShakeAnimation(); // Start shake animation
  };

  const startShakeAnimation = () => {
    const shakeInterval = setInterval(() => {
      setShake(!shake);
    }, 1000);
    setTimeout(() => clearInterval(shakeInterval), 10000); // Stop shaking after 10 seconds
  };

  const [shake, setShake] = useState(false); // State to trigger shake animation

  const shakeAnimation = {
    animation: shake ? "shake 0.5s" : "none",
    animationIterationCount: "infinite"
  };

  const playAgain = () => {
    setScore(0);
    setTimeLeft(60);
    setGameStarted(false);
    setEggsFound(0);
    setShowBrokenEgg(false);
    setOwlMessage("Click on the object to find Easter eggs");

    // Reset visibility of all eggs and hiding objects
    const eggs = document.querySelectorAll('.object');
    const hidingObjects = document.querySelectorAll('.hiding-object');

    eggs.forEach(egg => {
      egg.style.display = 'block';
    });

    hidingObjects.forEach(obj => {
      obj.style.display = 'block';
    });

    // Start shake animation for the new game
    startShakeAnimation();
  };

  const endGame = () => {
    setGameStarted(false);
    setShowBrokenEgg(true);
  };

  const displayLineByLine = () => {
    if (currentLineIndex < definitionLines.length) {
      const line = definitionLines[currentLineIndex];
      setDialogueText(line);
      setCurrentLineIndex((prev) => prev + 1);
      setTimeout(displayLineByLine, 3000); // Display next line after 3 seconds
    } else {
      setDialogueText("The owl has finished speaking!");
    }
  };

  const speak = (text) => {
    const speech = new SpeechSynthesisUtterance();
    speech.text = text;
    speech.lang = "en-US";
    speech.volume = 1;
    speech.rate = 1;
    speech.pitch = 1;
    window.speechSynthesis.speak(speech);
  };

  const findEgg = (points, event) => {
    if (event.target.style.display !== "none") {
      setScore((prev) => prev + points);
      event.target.style.display = "none"; // Hide the clicked egg
      setEggsFound((prev) => prev + 1); // Increment eggs found
      if (eggsFound + 1 === totalEggs) {
        setShowBrokenEgg(true); // Show broken egg
        endGame(); // End game when all eggs are found
      }
    }
  };

  const findHidingObject = (points, event) => {
    if (event.target.style.display !== "none") {
      setScore((prev) => prev + points);
      event.target.style.display = "none"; // Hide the clicked object
    }
  };

  const hintDeduction = () => {
    if (hintAvailable && score >= 30 && !hintUsed) {
      setScore((prev) => prev - 30);
      setHintUsed(true); // Mark hint as used
      setHintAvailable(false); // Disable further hint usage once one is used

      if (currentLineIndex < definitionLines.length) {
        const hint = definitionLines[currentLineIndex];
        setDialogueText(hint);
        speak(hint); // Speak the hint
        setCurrentLineIndex((prev) => prev + 1);
      } else {
        setDialogueText("No more hints!");
        speak("No more hints!");
      }
    } else if (score < 40) {
      setOwlMessage("Not enough points for a hint! Click on the object to find Easter eggs"); // Show combined message near owl
      setTimeout(() => setOwlMessage("Click on the object to find Easter eggs"), 3000); // Reset to default message after 3 seconds
      speak("Not enough points for a hint!");
    } else {
      setDialogueText("You have already used a hint!");
      speak("You have already used a hint!");
    }
  };

  return (
    <div
      className="App"
      style={{
        height: "100vh",
        overflow: "hidden",
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      {/* Confetti Effect when game is completed */}
      {showBrokenEgg && (
        <Confetti
          width={windowSize.width}
          height={windowSize.height}
          numberOfPieces={200}
          recycle={false}
          colors={['#FFD700', '#FF69B4', '#00FF00', '#4169E1', '#FF4500', '#8A2BE2']}
        />
      )}

      {/* Timer and Score at the sides */}
      <div id="timer" style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        padding: '10px 20px',
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        color: '#FFD700',
        borderRadius: '10px',
        fontSize: '20px',
        fontWeight: 'bold'
      }}>Time: {timeLeft}</div>
      <div id="scoreboard" style={{
        position: 'absolute',
        top: '20px',
        right: '20px',
        padding: '10px 20px',
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        color: '#00FF00',
        borderRadius: '10px',
        fontSize: '20px',
        fontWeight: 'bold'
      }}>Score: {score}</div>

      {/* Start button - only show if game hasn't started and reward isn't showing */}
      {!gameStarted && !showBrokenEgg && (
        <button id="start-btn" style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          padding: '15px 30px',
          fontSize: '24px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          borderRadius: '10px',
          cursor: 'pointer',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          transition: 'all 0.3s ease',
          ':hover': {
            backgroundColor: '#45a049',
            transform: 'translate(-50%, -50%) scale(1.05)'
          }
        }} onClick={startGame}>
          Start Game
        </button>
      )}

      {/* Broken Egg and Score Container */}
      {showBrokenEgg && (
        <div id="broken-egg-container" style={{
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          zIndex: 50,
          backgroundColor: "rgba(255, 255, 255, 0.95)",
          padding: "40px",
          borderRadius: "20px",
          textAlign: "center",
          boxShadow: "0 8px 16px rgba(0, 0, 0, 0.3)",
          color: "#333",
          minWidth: "300px",
          animation: "popIn 0.5s ease-out"
        }}>
          <div style={{ marginBottom: "20px" }}>
            <Star color="gold" size={48} style={{ marginBottom: "10px" }} />
            <img 
              src={brokenEggImage} 
              alt="Broken Egg" 
              style={{ 
                width: "150px",
                marginTop: "10px",
                animation: "bounce 1s infinite"
              }} 
            />
          </div>
          <h2 style={{
            color: '#FF4081',
            fontSize: '28px',
            marginBottom: '15px',
            fontWeight: 'bold',
            textShadow: '2px 2px 4px rgba(0,0,0,0.1)'
          }}>Congratulations!</h2>
          <p style={{
            color: '#2196F3',
            fontSize: '20px',
            marginBottom: '20px'
          }}>Your final score is: {score}</p>
          <button onClick={playAgain} style={{
            marginTop: "20px",
            padding: "12px 30px",
            backgroundColor: "#FF4081",
            color: "white",
            border: "none",
            borderRadius: "25px",
            cursor: "pointer",
            fontSize: "18px",
            fontWeight: "bold",
            boxShadow: "0 4px 8px rgba(255,64,129,0.3)",
            transition: "all 0.3s ease",
            ':hover': {
              backgroundColor: "#E91E63",
              transform: "scale(1.05)"
            }
          }}>Play Again</button>
        </div>
      )}

      {/* Exit Button */}
      <button style={{
        position: 'absolute',
        bottom: '20px',
        right: '20px',
        padding: '10px 25px',
        backgroundColor: '#FF4444',
        color: 'white',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontSize: '16px',
        fontWeight: 'bold',
        boxShadow: '0 4px 8px rgba(255,68,68,0.3)',
        transition: 'all 0.3s ease',
        zIndex: 100,
      }}>
        Exit Game
      </button>

      {/* Owl */}
      <img
        src={owlImage}
        id="owl"
        alt="Owl"
        style={{
          position: "absolute",
          top: "10%", // Adjust this value to change its vertical position
          left: "10%", // Adjust this value to change its horizontal position
          cursor: "pointer",
          zIndex: 20,
          width: "8%",
        }}
        onClick={hintDeduction} // Owl provides hints and deducts points
      />

      {/* Owl Dialogue Box */}
      {owlMessage && (
        <div id="owl-dialogue" style={{
          position: "absolute",
          top: "15%",
          left: "18%",
          backgroundColor: "rgba(0, 0, 0, 0.8)",
          padding: "15px",
          borderRadius: "15px",
          color: "#FFD700",
          maxWidth: "250px",
          textAlign: "center",
          zIndex: 30,
          fontSize: "16px",
          fontWeight: "500",
          boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
          border: "2px solid #FFD700"
        }}>
          {owlMessage}
        </div>
      )}

      {/* Hiding objects and Easter eggs */}
      <img
        src={treeImage}
        className="hiding-object"
        style={{
          position: "absolute",
          top: "36%",
          left: "80%",
          width: "10%",
          height: "auto",
          cursor: gameStarted ? "pointer" : "not-allowed",
          ...shakeAnimation
        }}
        alt="Tree"
        onClick={(e) => gameStarted && findHidingObject(10, e)}
      />
      <img
        src={bushImage}
        className="hiding-object"
        style={{
          position: "absolute",
          top: "40%",
          left: "60%",
          width: "10%",
          height: "auto",
          cursor: gameStarted ? "pointer" : "not-allowed",
          ...shakeAnimation
        }}
        alt="Bush"
        onClick={(e) => gameStarted && findHidingObject(15, e)}
      />
      <img
        src={rockImage}
        className="hiding-object"
        style={{
          position: "absolute",
          top: "70%",
          left: "20%",
          width: "10%",
          height: "auto",
          cursor: gameStarted ? "pointer" : "not-allowed",
        }}
        alt="Rock"
        onClick={(e) => gameStarted && findHidingObject(20, e)}
      />
      <img
        src={egg1Image}
        className="object"
        id="egg1"
        style={{
          position: "absolute",
          top: "37%",
          left: "81%",
          width: "3%",
          height: "auto",
          cursor: gameStarted ? "pointer" : "not-allowed",
        }}
        onClick={(e) => gameStarted && findEgg(10, e)}
        alt="Egg 1"
      />
      <img
        src={egg2Image}
        className="object"
        id="egg2"
        style={{
          position: "absolute",
          top: "51%",
          left: "63%",
          width: "5%",
          height: "auto",
          cursor: gameStarted ? "pointer" : "not-allowed",
        }}
        onClick={(e) => gameStarted && findEgg(20, e)}
        alt="Egg 2"
      />
      <img
        src={egg3Image}
        className="object"
        id="egg3"
        style={{
          position: "absolute",
          top: "75%",
          left: "22%",
          width: "5%",
          height: "auto",
          cursor: gameStarted ? "pointer" : "not-allowed",
        }}
        onClick={(e) => gameStarted && findEgg(15, e)}
        alt="Egg 3"
      />
    </div>
  );
}

export default App;
